private const val OFFERS_PATH = "/offers"
private const val CALENDAR_TERMS_PATH_V0 = "/v0/calendar-terms"
private const val ROOT_PATH_V0 = "/"

class MockIonWebAPI(
    private val ionMapper: IIonMapper,
    private val dispatcher: CoroutineDispatcher = Dispatchers.IO
) : IIonWebAPI {

    override suspend fun <T> getFromURI(uri: URI, klass: Class<T>, accept: String, bearerToken: String): T {
        val responseBody: String = get(uri)
        return ionMapper.parse(responseBody, klass)
    }

    override suspend fun addClassSectionToCoreFavourites(uri: URI, bearerToken: String): String {
        return get(uri)
    }

    override suspend fun removeClassSectionFromCoreFavourites(
        uri: URI,
        bearerToken: String
    ){ get(uri)
    }

    override suspend fun <T> getFromURIWithoutAuth(uri: URI, klass: Class<T>, accept: String): T {
        val responseBody: String = get(uri)
        return ionMapper.parse(responseBody, klass)
    }

    override suspend fun <T> pollCore(uri: URI, klass: Class<T>, accept: String): T {
        val responseBody: String = get(uri)
        return ionMapper.parse(responseBody, klass)
    }

    override suspend fun getAuthMethodsArray(uri: URI, accept: String): List<AuthMethod> {
        return listOf()
    }

    override suspend fun <T> loginWithEmail(
        uri: URI,
        accept: String,
        klass: Class<T>,
        body:RequestBody
    ): T {
        val responseBody: String = get(uri)
        return ionMapper.parse(responseBody, klass)
    }

    /**
     *  This is using the IO Dispacher, which is optimized to perform disk or network I/O
     *  outside of the main thread. Examples include using the Room component,
     *  reading from or writing to files, and running any network operations.
     */
    private suspend fun get(uri: URI): String =
        withContext(dispatcher) {
            //Chaos filter which simulates request delay, from 50ms to 500ms
            val randomdelay = Random.nextLong(50, 500)
            delay(randomdelay)
            route(uri)
        }

    private fun route(uri: URI): String = when (uri.path) {
        ROOT_PATH_V0 -> root
        PROGRAMMES_PATH_V0 -> allProgrammesMock
        COURSES_PATH_V0 -> allCoursesMock
        "${PROGRAMMES_PATH_V0}/1" -> leicMock
